mapper.select:
select * from (select rowNum rNum,post_no,writer,title,THUMNAIL,hit,BOOKMARK from
(select * from RECIPE_POST


    title like #{find_name}     or ingredient like #{find_name}

order by POST_NO desc))
where rNum >=#{startrow} and rNum <=#{endrow}

select*from(select NICKNAME,POST_NO,WRITER from
RECIPE_POST left join MEMBER
on (RECIPE_POST.WRITER = MEMBER.USERID)



        title like #{find_name} or ingredient like #{find_name}

 order by POST_NO desc)where rowNum >=#{startrow} and rowNum <=#{endrow}

select * from(select rownum rNum,c.* from (select a.post_no, nvl(round(avg(score),2),0) as score
from RECIPE_POST a left join RECIPE_REPLY b on(b.post_no=a.POST_NO)



        title like #{find_name} or ingredient like #{find_name}

group by a.post_no order by a.POST_NO desc)c)d
where d.rNum >=#{startrow} and d.rNum <=#{endrow}
